# View para la página de about
def about(request):
    fake = Faker()
    if not Contacts.objects.exists():
        for _ in range(30):
            Contacts.objects.create(
                full_name=fake.name(),                         # Genera un nombre falso
                address=fake.address(),                        # Genera una dirección falsa
                phone=fake.phone_number(),                     # Genera un número de teléfono falso
                email=fake.email(),                            # Genera un correo falso
                active=fake.boolean(chance_of_getting_true=70) # Genera un valor booleano falso
            )
    # Obtenemos los contactos activos
    active_contacts = Contacts.objects.filter(active=True)
    return render(request, 'shopapp/about.html', {'contacts': active_contacts})
